import React, {useState} from "react";
import { Link, useNavigate } from "react-router-dom";

import FirebaseUserService from "../services/FirebaseUserService"
import FirebaseContext from "../utils/FirebaseContext"

const HomePage = ({setLogged,setShowToast,setToast}) =>
    <FirebaseContext.Consumer>
        {(firebase) => <Home
                            firebase = {firebase}
                            setLogged = {setLogged}
                            setShowToast={setShowToast}
                            setToast = {setToast}/>}
    </FirebaseContext.Consumer>    

function Home(props) {

    const [login,setLogin] = useState('')
    const [password,setPassword] = useState('')
    const [loading,setLoading] = useState(false)
    const [validate, setValidate] = useState({login:'',password:''})

    const navigate = useNavigate()

    const validateFields = () => {
        let res = true
        setValidate({login:'', password:''})

        if(login==='' || password === ''){
            props.setToast({header})
        }
    }


    const handleSubmit = (event) => {
        event.preventDefault()
        setLoading(true)
        FirebaseUserService.login(
            props.firebase.getAuthentication(),
            login,
            password,
            (user) => {
                if (user != null){
                    setLoading(false)
                    props.firebase.setUser(user)
                    props.setLogged(true)
                    navigate('/listStudent')
                } else {
                    setLoading(false)
                }
            }
        )
    }

    const renderSubmitButton = () => {
        if (loading) {
            return (
                <div style={{ paddingTop: 20 }}>
                    <button className="btn btn-primary" type="button" disabled>
                        <span className="spinner-border spinner-border-sm" role="status" aria-hidden="true"></span>
                        <span style={{ marginLeft: 10 }}>Carregando...</span>
                    </button>
                </div>
            )
        }
        return (
            <>
                <div className="form-group" style={{ paddingTop: 20 }}>
                    <input type="submit" value="Efetuar Login" className="btn btn-primary" />
                </div>
            </>
        )
    }

    return (
        <div className="content-login" style={{ marginTop: 50 }}>
            <main style={{ width: '40%' }}>
                <h2>Login</h2>
                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label>Login* </label>
                        <input type="text"
                            className="form-control"
                            value={(login == null || login === undefined) ? "" : login}
                            name="login"
                            onChange={(event) => { setLogin(event.target.value) }} />
                    </div>
                    <div className="form-group">
                        <label>Senha* </label>
                        <input type="password"
                            className="form-control"
                            value={password ?? ""}
                            name="password"
                            onChange={(event) => { setPassword(event.target.value) }} />
                    </div>
                    {renderSubmitButton()}
                </form>
            </main>
            <nav>
                <Link to="/about">About</Link>
            </nav>
        </div>
    );
}

export default HomePage
