import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import FirebaseProfessorService from "../../../services/FirebaseProfessorService";
import FirebaseContext from "../../../utils/FirebaseContext";
//import axios from "axios";

import ProfessorTableRow from "./ProfessorTableRow";
import RestrictPage from "../../../utils/RestrictPage";


const ListProfessorPage = ({ setShowToast, setToast }) =>
<FirebaseContext.Consumer>
{
    (firebase) => {
        return (
            <RestrictPage isLogged={firebase.getUser() != null} isVerified = {firebase.getUser() != null && firebase.getUser().emailVerified != false}>
                <ListProfessor
                    firebase={firebase}
                    setShowToast={setShowToast}
                    setToast={setToast} />
            </RestrictPage>
        )
    }
}
</FirebaseContext.Consumer>


function ListProfessor(props) {

    const [professors, setProfessors] = useState([])
    const [loading, setLoading] = useState(false)

    useEffect(
        () => {
            /*
            axios.get("http://localhost:3002/crud/professors/list")
            .then(
                (response) => {
                    setProfessors(response.data)
                }
            )
            .catch (error => console.log(error))
            */
           setLoading(true)
            FirebaseProfessorService.list_onSnapshot(
                props.firebase.getFirestoreDb(),
                (professors) => {
                    setLoading(false)
                    setProfessors(professors)
                }
            )
        }, [props.firebase]
    )

    function deleteProfessorById(_id) {
        let professorsTemp = professors
        for (let i = 0; i < professorsTemp.length; i++) {
            if (professorsTemp[i]._id === _id) {
                professorsTemp.splice(i, 1)
            }
        }
        setProfessors([...professorsTemp])
    }

    function renderTable() {

        if (loading) {
            //mostrar um spinner!
            return (
                <div style={{
                    display:'flex',
                    flexDirection:'column',
                    justifyContent:'center',
                    alignItems:'center',
                    padding:100
                }}>
                    <div className="spinner-border" 
                     style={{width: '3rem', height: '3rem'}} 
                     role="status" />
                     Carregando...
                </div>
            )
        }   
            return (
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Nome</th>
                            <th>IRA</th>
                            <th>Nome</th>
                            <th colSpan={2} style={{ textAlign: "center" }}></th>
                        </tr>
                    </thead>
                    <tbody>
                        {renderTableBody()}
                    </tbody>
                </table>
            )
        }


    return (
        <>
            <main>
                <h2>
                    Listar Professores
                </h2>
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Nome</th>
                            <th>Universidade</th>
                            <th>Titulação</th>
                            <th colSpan={2} style={{ textAlign: "center" }}></th>
                        </tr>
                    </thead>
                    <tbody>
                        {generateTable()}
                    </tbody>
                </table>
            </main>
            <nav>
                <Link to="/">Home</Link>
            </nav>
        </>
    );
}

export default ListProfessorPage
